import {TaskType} from "../Todolist";

export const taskReducer = (state: Array<TaskType>, action: tasksReducerType) => {
    switch (action.title) {
        case 'REMOVE-TASKS': {
            console.log('REMOVE TASKS')
            return state
        }

        default:
            return state
    }
}

export type tasksReducerType = removeTaskACType
export type removeTaskACType=ReturnType<typeof removeTaskAC> //auto typesation

export const removeTaskAC = () => {
    return {
        title: 'REMOVE-TASKS'
    } as const
}

