import React, {useState, ChangeEvent, KeyboardEvent} from 'react';

type TaskType = {
    id: string
    title: string
    isDone: boolean
}

type PropsType = {
    title: string
    tasks: Array<TaskType>
    removeTask: (id: string) => void
    tasksFilter: (filterValue: string) => void
    addTask: (newTitle: string) => void
}


export function Todolist(props: PropsType) {
    // let [valueButton, setValueButton] = useState('All')
    //
    // const tasksFilter = (filterValue: string) => {
    //     setValueButton(filterValue)
    // }
    //
    // let prokladka = props.tasks
    // if (valueButton === "Active") {
    //     prokladka = props.tasks.filter(el => !el.isDone)
    // }
    // if (valueButton === "Completed") {
    //     prokladka = props.tasks.filter(el => el.isDone)
    // }
    let [newTitle, setNewTitle] = useState('')

    const addTaskHandler = () => {
        props.addTask(newTitle)
        setNewTitle('')
    }

    const onKeyPressHandler = (event: KeyboardEvent<HTMLInputElement>) => {
        // if (event.charCode === 13) {
        if (event.key === 'Enter') {
            addTaskHandler()
        }
    }

    const onChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setNewTitle(event.currentTarget.value)
    }

    const filterHandler = (filterValue: string) => {
        props.tasksFilter(filterValue)
    }

    const removeTaskHandler = (elID: string) => {
        props.removeTask(elID)
    }

    return <div>
        <h3>{props.title}</h3>
        <div>
            <input value={newTitle}
                   onKeyPress={onKeyPressHandler}
                   onChange={onChangeHandler}/>

            {/*<input onChange={(event) => setNewTitle(event.currentTarget.value)}/>*/}
            <button onClick={addTaskHandler}>+</button>
        </div>
        <ul>
            {props.tasks.map((el) => {
                return (
                    <li key={el.id}>
                        {/*<button onClick={() => props.removeTask(el.id)}>X</button>*/}
                        <button onClick={() => removeTaskHandler(el.id)}>X</button>
                        <input type="checkbox" checked={el.isDone}/>
                        <span>{el.title}</span>
                    </li>
                )
            })}
        </ul>
        <div>
            <button onClick={() => filterHandler('All')}>All</button>
            <button onClick={() => filterHandler('Active')}>Active</button>
            <button onClick={() => filterHandler('Completed')}>Completed</button>
        </div>
    </div>
}
