import React, {useState} from 'react';

type TaskType = {
    id: string
    title: string
    isDone: boolean
}

type PropsType = {
    title: string
    tasks: Array<TaskType>
    removeTask: (id: string) => void
    tasksFilter: (filterValue: string) => void
    addTask: () => void
}


export function Todolist(props: PropsType) {
    // let [valueButton, setValueButton] = useState('All')
    //
    // const tasksFilter = (filterValue: string) => {
    //     setValueButton(filterValue)
    // }
    //
    // let prokladka = props.tasks
    // if (valueButton === "Active") {
    //     prokladka = props.tasks.filter(el => !el.isDone)
    // }
    // if (valueButton === "Completed") {
    //     prokladka = props.tasks.filter(el => el.isDone)
    // }
    return <div>
        <h3>{props.title}</h3>
        <div>
            <input onChange={() => event.currentTarget}/>
            <button onClick={() => {
                props.addTask()
            }}>+
            </button>
        </div>
        <ul>
            {props.tasks.map((el) => {
                return (
                    <li key={el.id}>
                        <button onClick={() => props.removeTask(el.id)}>X</button>
                        <input type="checkbox" checked={el.isDone}/>
                        <span>{el.title}</span>
                    </li>
                )
            })}
        </ul>
        <div>
            <button onClick={() => props.tasksFilter("All")}>All</button>
            <button onClick={() => props.tasksFilter("Active")}>Active</button>
            <button onClick={() => props.tasksFilter('Completed')}>Completed</button>
        </div>
    </div>
}
